        return _allowance(owner, spender);
    }

    function _allowance(
        address owner,
        address spender
    ) internal view virtual returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See `IERC20.approve`.
     *
     * Requirements:
